import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import CartItem from "../components/CartItem";
import { useEffect, useState } from "react";
import { removeAll } from "../redux/Slices/cartSlice";
import { useDispatch } from "react-redux";

const Cart = () => {
  const {cart} = useSelector((state) => state);
  console.log(cart);

  const dispatch = useDispatch();

  const [totalamount, settotalamount] = useState(0);

  useEffect(()=>{
    settotalamount(cart.reduce((acc,curr)=>acc+curr.price,0))
  },[cart])

  return( 
  <div>
   {
    cart.length > 0 ? 

    <div className="flex justify-evenly">
   
   <div className="w-2/4 min-h-screen flex flex-wrap  justify-center bg-slate-500">
   { cart.map((item,index) => (<CartItem key={item.id} item={item} itemIndex={index}/>))}
   </div>

    <div className=" px-20 w-2/4 flex flex-col items-start justify-evenly max-h-screen text-xl font-semibold">
      <div>Your Cart</div>
      <button onClick={()=>{dispatch(removeAll())}} >remove All</button>
      <div>
        <div>total items : <span className="font-bold">{cart.length}</span></div>
        <div>total amount : <span className="font-bold">Rs.{totalamount}</span></div>
      </div>
      <button className="bg-slate-400 border-2 border-slate-800 px-5 rounded-md " >checkOut</button>
    </div>

    </div>
    : 
    <div className="flex flex-col items-center justify-center text-xl font-bold">
      <h2 >Empty Cart</h2>
      <NavLink to="/" className="bg-slate-200 px-3 border-2 border-slate-600">Shop Now</NavLink>
    </div>
   }
  </div>
);
}
export default Cart;
